import React, { useState, useEffect } from "react";
import { render } from "react-dom";
import socketIOClient from "socket.io-client";
import PropTypes from 'prop-types';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';




import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import Tooltip from '@material-ui/core/Tooltip';
import IconButton from '@material-ui/core/IconButton';
import { withStyles } from '@material-ui/core/styles';
import SearchIcon from '@material-ui/icons/Search';
import RefreshIcon from '@material-ui/icons/Refresh';
import SendIcon from '@material-ui/icons/Send';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import Avatar from '@material-ui/core/Avatar';
import ListItemAvatar from '@material-ui/core/ListItemAvatar';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import {styles} from './ChatBoxCSS';


import { API_URL } from '../../config/config';
const socket = socketIOClient(API_URL);





class ChatBox extends React.Component
{

    constructor() {
        super();
        this.state ={chatinput:"",
        chatmessages:[],
        conversationId:""};
        this.chatarea = React.createRef();
        //socket : socketIOClient(ENDPOINT)

      }

      // this is where we are connecting to with sockets,


    // this is where we are connecting to with sockets,
    scrollToBottom() {
      //this.chatarea.current.scrollTop = this.chatarea.current.scrollHeight;
    }
    onInput=(e)=> {
      this.setState({chatinput:e.target.value})

      } 
    sendMessage=()=>
    {
      console.log("button is clicked");

      console.log(socket);
      socket.emit("chat message",this.state.chatinput);
      //sendMessageText(this.props.socket,this.state.chatinput)
      console.log("message sent");
      this.setState({chatinput:""});
    }

    componentDidMount() {
      //this.state.socket.on("chat message", data => {
    //const {socket} = this.props;

    socket.on("chat message", data => {
        console.log("received" + data);
        this.setState({
          chatmessages: [...this.state.chatmessages, data],
        });
        //this.scrollToBottom();
    });
    console.log("initializzed");
    }

 render()
      {
      return(
        <Paper className={styles.paper}>
        <div className={styles.contentHeader}>
          Subhasis Padhy
         </div>

        <div className={styles.contentWrapper} ref={this.chatarea}>

    <List>



     {
      this.state.chatmessages.map((chat) => {

        return (  
          <ListItem className={styles.message}>
          <ListItemIcon>
          <Avatar alt="Remy Sharp" src="https://material-ui.com/static/images/avatar/1.jpg" />
         </ListItemIcon>
         <ListItemText primary="Subhasis Padhy">Subhasis Padhy</ListItemText>
         <ListItemText secondary="online" align="left"></ListItemText>
         </ListItem>




        )




      })
    }

<ListItem alignItems="flex-start">
        <ListItemAvatar>
          <Avatar alt="Remy Sharp" src="https://material-ui.com/static/images/avatar/1.jpg" />
        </ListItemAvatar>
        <ListItemText
          primary="Brunch this weekend?"
          secondary={
            <React.Fragment>
              <Typography
                component="span"
                variant="body2"
                className={styles.inline}
                color="textPrimary"
              >
                Ali Connors
              </Typography>
              {" — I'll be in your neighborhood doing errands this…"}
            </React.Fragment>
          }
        />
        <ListItemText secondary="online" align="left"></ListItemText>
      </ListItem>
     </List>
      </div>
      <AppBar className={styles.searchBar} position="static" color="default" elevation={0}>
        <Toolbar>
          <Grid container spacing={2} alignItems="center">

            <Grid item xs>
              <TextField
                fullWidth
                placeholder="type a message"
                InputProps={{
                  disableUnderline: true,
                  className: styles.searchInput,
                }}
                onChange={this.onInput} value={this.state.chatinput} 
              />
            </Grid>
            <Grid item>
                <button onClick={this.sendMessage}><SendIcon></SendIcon></button>


            </Grid>
          </Grid>
        </Toolbar>
      </AppBar>

    </Paper>
      );

      }
}

export default withStyles(styles)(ChatBox);
