import React, { useState, useEffect } from "react";
import { render } from "react-dom";
import socketIOClient from "socket.io-client";
import PropTypes from 'prop-types';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import Tooltip from '@material-ui/core/Tooltip';
import IconButton from '@material-ui/core/IconButton';
import { withStyles } from '@material-ui/core/styles';
import SearchIcon from '@material-ui/icons/Search';
import RefreshIcon from '@material-ui/icons/Refresh';
import SendIcon from '@material-ui/icons/Send';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import {styles} from './ChatBoxCSS'




class ChatBox extends React.Component
{
    constructor() {
        super();
        this.state ={chatinput:"",
        chatmessages:[],
        conversationId:""
        //socket : socketIOClient(ENDPOINT)
      }
    }
      render()
      {
      return(
        <Paper className={styles.paper}>
        <div className={styles.contentHeader}>
          Subhasis Padhy
         </div>

        <div className={styles.contentWrapper}>
    <List>

    <ListItem className={styles.message}>

     <Card className={styles.message} variant="outlined">
      <CardContent>     
        <Typography variant="body2" component="p">
          well meaning and kindly.

        </Typography>
      </CardContent> 
     </Card>
     </ListItem>

     <ListItem className={styles.message}>

     <Card className={styles.message} variant="outlined">
      <CardContent>     
        <Typography variant="body2" component="p">
          well meaning and kindly.

        </Typography>
      </CardContent> 
     </Card>
     </ListItem>
     </List>
      </div>
      <AppBar className={styles.searchBar} position="static" color="default" elevation={0}>
        <Toolbar>
          <Grid container spacing={2} alignItems="center">

            <Grid item xs>
              <TextField
                fullWidth
                placeholder="type a message"
                InputProps={{
                  disableUnderline: true,
                  className: styles.searchInput,
                }}
              />
            </Grid>
            <Grid item>
                <button><SendIcon></SendIcon></button>


            </Grid>
          </Grid>
        </Toolbar>
      </AppBar>

    </Paper>
      );

      }
}

export default withStyles(styles)(ChatBox);
